import{_ as e,c as t,o as a,V as s}from"./chunks/framework.99df3b45.js";const y=JSON.parse('{"title":"Instrucciones de transferencia de datos","description":"","frontmatter":{},"headers":[],"relativePath":"como-usar/instrucciones/transferencia-de-datos.md","lastUpdated":1681770192000}'),o={name:"como-usar/instrucciones/transferencia-de-datos.md"},n=s('<h1 id="instrucciones-de-transferencia-de-datos" tabindex="-1">Instrucciones de transferencia de datos <a class="header-anchor" href="#instrucciones-de-transferencia-de-datos" aria-label="Permalink to &quot;Instrucciones de transferencia de datos&quot;">​</a></h1><p><a href="./listado">← Volver al listado</a></p><h2 id="mov" tabindex="-1"><code>MOV</code> <a class="header-anchor" href="#mov" aria-label="Permalink to &quot;`MOV`&quot;">​</a></h2><p>Copia <em>fuente</em> en <em>dest</em>.</p><div class="language-vonsim"><button title="Copy Code" class="copy"></button><span class="lang">vonsim</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">MOV</span><span style="color:#A6ACCD;"> dest</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> fuente</span></span></code></pre></div><p>Las posibles combinaciones de <code>dest,fuente</code> están detalladas <a href="./../modos-de-direccionamiento#combinaciones-dest-fuente">aquí</a>.</p><h2 id="push" tabindex="-1"><code>PUSH</code> <a class="header-anchor" href="#push" aria-label="Permalink to &quot;`PUSH`&quot;">​</a></h2><p>Apila <em>fuente</em> en la <a href="./../pila">pila</a>. <em>fuente</em> solo puede ser un registro de 16 bits.</p><div class="language-vonsim"><button title="Copy Code" class="copy"></button><span class="lang">vonsim</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">PUSH</span><span style="color:#A6ACCD;"> fuente</span></span></code></pre></div><h2 id="pop" tabindex="-1"><code>POP</code> <a class="header-anchor" href="#pop" aria-label="Permalink to &quot;`POP`&quot;">​</a></h2><p>Desapila el tope de la <a href="./../pila">pila</a> y lo carga en <em>dest</em>. <em>dest</em> solo puede ser un registro de 16 bits.</p><div class="language-vonsim"><button title="Copy Code" class="copy"></button><span class="lang">vonsim</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">POP</span><span style="color:#A6ACCD;"> dest</span></span></code></pre></div><h2 id="pushf" tabindex="-1"><code>PUSHF</code> <a class="header-anchor" href="#pushf" aria-label="Permalink to &quot;`PUSHF`&quot;">​</a></h2><p>Apila los flags.</p><div class="language-vonsim"><button title="Copy Code" class="copy"></button><span class="lang">vonsim</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">PUSHF</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;font-style:italic;">; no recibe operandos</span></span></code></pre></div><h2 id="popf" tabindex="-1"><code>POPF</code> <a class="header-anchor" href="#popf" aria-label="Permalink to &quot;`POPF`&quot;">​</a></h2><p>Desapila los flags y los guarda en la ALU.</p><div class="language-vonsim"><button title="Copy Code" class="copy"></button><span class="lang">vonsim</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">POP</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;font-style:italic;">; no recibe operandos</span></span></code></pre></div><h2 id="in" tabindex="-1"><code>IN</code> <a class="header-anchor" href="#in" aria-label="Permalink to &quot;`IN`&quot;">​</a></h2><p>Carga el valor del puerto <em>fuente</em> en <em>dest</em>. Sirve para leer los registros de los <a href="./../dispositivos/listado">dispositivos</a> mediante la memoria E/S.</p><div class="language-vonsim"><button title="Copy Code" class="copy"></button><span class="lang">vonsim</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">IN</span><span style="color:#A6ACCD;"> dest</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> fuente</span></span></code></pre></div><p>Las posibles combinaciones de operandos son:</p><table><thead><tr><th style="text-align:center;"><em>dest</em></th><th style="text-align:center;"><em>fuente</em></th></tr></thead><tbody><tr><td style="text-align:center;"><code>AL</code></td><td style="text-align:center;"><em>puerto</em></td></tr><tr><td style="text-align:center;"><code>AX</code></td><td style="text-align:center;"><em>puerto</em></td></tr><tr><td style="text-align:center;"><code>AL</code></td><td style="text-align:center;"><code>DX</code></td></tr><tr><td style="text-align:center;"><code>AX</code></td><td style="text-align:center;"><code>DX</code></td></tr></tbody></table><p>donde <em>puerto</em> es una dirección entre 0 y 255 (puede ser un operando inmediato o una etiqueta).</p><h2 id="out" tabindex="-1"><code>OUT</code> <a class="header-anchor" href="#out" aria-label="Permalink to &quot;`OUT`&quot;">​</a></h2><p>Carga en el puerto <em>dest</em> el valor en <em>fuente</em>. Sirve para escribir en los registros de los <a href="./../dispositivos/listado">dispositivos</a> mediante la memoria E/S.</p><div class="language-vonsim"><button title="Copy Code" class="copy"></button><span class="lang">vonsim</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">OUT</span><span style="color:#A6ACCD;"> dest</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> fuente</span></span></code></pre></div><p>Las posibles combinaciones de operandos son:</p><table><thead><tr><th style="text-align:center;"><em>dest</em></th><th style="text-align:center;"><em>fuente</em></th></tr></thead><tbody><tr><td style="text-align:center;"><em>puerto</em></td><td style="text-align:center;"><code>AL</code></td></tr><tr><td style="text-align:center;"><em>puerto</em></td><td style="text-align:center;"><code>AX</code></td></tr><tr><td style="text-align:center;"><code>DX</code></td><td style="text-align:center;"><code>AL</code></td></tr><tr><td style="text-align:center;"><code>DX</code></td><td style="text-align:center;"><code>AX</code></td></tr></tbody></table><p>donde <em>puerto</em> es una dirección entre 0 y 255 (puede ser un operando inmediato o una etiqueta).</p>',30),l=[n];function d(i,r,c,p,m,u){return a(),t("div",null,l)}const g=e(o,[["render",d]]);export{y as __pageData,g as default};
